mapper.insert:
insert into t_wwx_device_dict (
	dict_id,
	device_id
) values (
	#{dictId,jdbcType=BIGINT},
	#{deviceId,jdbcType=BIGINT}
)

mapper.select:
select id,
	dict_id,
	device_id
from t_wwx_device_dict
where dict_id = #{dictId,jdbcType=BIGINT} 
and device_id = #{deviceId,jdbcType=BIGINT}
